mapper.update:
update CashBillReceipt
set lastModifiedTime=#{lastModifiedTime}, receiptState=#{receiptState}, accountId=#{accountId}, total=#{total}, itemList=#{itemList, typeHandler=MyJsonHandler}
where dayId=#{dayId} and createTime=#{createTime}

mapper.select:
select * from CashBillReceipt


        memberID=#{memberId}


        AND clerkName=#{clerkName}


        AND stockName=#{stockName}


        AND createTime >= #{begin}


        AND createTime <= #{end}

select * from ChargeBillReceipt


        operatorId=#{operatorId}


        AND dayId=#{dayId}


        AND receiptState=#{receiptState}


        AND createTime >= #{createTimeFloor}


        AND createTime <= #{createTimeCeil}


        AND lastModifiedTime >= #{lastModifiedTimeFloor}


        AND lastModifiedTime <= #{lastModifiedTimeCeil}
