mapper.update:
update ChargeBillReceipt
set lastModifiedTime=#{lastModifiedTime}, receiptState=#{receiptState}, retailerId=#{retailerId},  transferList=#{transferList, typeHandler=MyJsonHandler},total=#{total}
where dayId=#{dayId} and createTime=#{createTime}

mapper.select:
select * from ChargeBillReceipt


        clientId=#{clientId}


        AND clerkName=#{clerkName}


        AND createTime >= #{begin}


        AND createTime <= #{end}
