mapper.update:
update PaymentBillReceipt
set lastModifiedTime=#{lastModifiedTime}, receiptState=#{receiptState}, supplierId=#{supplierId},  transferList=#{transferList, typeHandler=MyJsonHandler},total=#{total}
where dayId=#{dayId} and createTime=#{createTime}

mapper.select:
select * from PaymentBillReceipt


        clientId=#{clientId}


        AND clerkName=#{clerkName}


        AND createTime >= #{begin}


        AND createTime <= #{end}
